import{_ as s,C as e,c as i,o as l,aA as t,G as o}from"./chunks/framework.CP3aFJGh.js";const u=JSON.parse('{"title":"相关国际纪念日","description":"","frontmatter":{"title":"相关国际纪念日"},"headers":[],"relativePath":"documents/day-and-festival.md","filePath":"documents/day-and-festival.md","lastUpdated":1740777290000}'),d={name:"documents/day-and-festival.md"};function h(c,a,f,_,p,b){const n=e("NolebaseGitContributors"),r=e("NolebaseGitChangelog");return l(),i("div",null,[a[0]||(a[0]=t('<h1 id="国际节日" tabindex="-1">国际节日 <a class="header-anchor" href="#国际节日" aria-label="Permalink to &quot;国际节日&quot;">​</a></h1><h2 id="国际跨性别现身日" tabindex="-1">国际跨性别现身日 <a class="header-anchor" href="#国际跨性别现身日" aria-label="Permalink to &quot;国际跨性别现身日&quot;">​</a></h2><p>国际跨性别现身日（International Transgender Day of Visibility），日期为每年的3月31日。是为了专门庆祝跨性别者，并提高全球关注跨性别者面临歧视的意识而设立的节日。在这一天，人们鼓励跨性别者勇敢地展示自己的身份和存在，以消除对他们的歧视和偏见。这个节日强调跨性别者的重要性，让他们的声音被听到，让他们的故事被分享，同时也提醒公众关注和支持跨性别群体的权益。</p><h2 id="国际不再恐同恐跨恐双日" tabindex="-1">国际不再恐同恐跨恐双日 <a class="header-anchor" href="#国际不再恐同恐跨恐双日" aria-label="Permalink to &quot;国际不再恐同恐跨恐双日&quot;">​</a></h2><p>国际不再恐同恐跨恐双日（International Day against Homophobia, Transphobia, and Biphobia），日期为每年的5月17日。最初称国际不再恐同日，是为了纪念世界卫生组织在1990年5月17日，将同性恋从精神疾病分类中移除的历史事件。此后，这个日子也被用来呼吁停止对同性恋、跨性别和双性恋个体的歧视和偏见，促进包容和尊重性别和性取向的多样性。在国际不再恐同恐跨恐双日，人们举办各种活动、宣传和社区活动，以提高公众对LGBTQ+群体权益的认识，推动平等和尊重。</p><h2 id="跨性别意识周" tabindex="-1">跨性别意识周 <a class="header-anchor" href="#跨性别意识周" aria-label="Permalink to &quot;跨性别意识周&quot;">​</a></h2><p>跨性别意识周（Transgender Awareness Week），日期为每年的11月13日至11月19日，是跨性别追悼日的前一周的庆祝活动。该活动旨在提高公众对跨性别者的认识和理解。</p><h2 id="跨性别追悼日" tabindex="-1">跨性别追悼日 <a class="header-anchor" href="#跨性别追悼日" aria-label="Permalink to &quot;跨性别追悼日&quot;">​</a></h2><p>跨性别追悼日（Transgender Day of Remembrance），也称跨性别死难者纪念日，日期为每年的11月20日。是为了悼念反跨性别仇恨暴力谋杀事件的受害者，并提高对跨性别群体所受暴力的认知度而设立的节日。在这一天，人们纪念那些因为跨性别身份而遭受暴力和仇恨而丧生的人。他们可能是因为其性别认同或性别表达而成为暴力行为的受害者，而这些暴力行为常常是由于对跨性别群体的歧视和偏见。这个节日不仅是为了纪念那些逝去的生命，也是为了呼吁社会关注跨性别者所面临的暴力问题，并促进对跨性别群体的尊重和理解。</p><hr><h1 id="社群倡议" tabindex="-1">社群倡议 <a class="header-anchor" href="#社群倡议" aria-label="Permalink to &quot;社群倡议&quot;">​</a></h1><p>国内跨性别社群曾提议将每年的3月9日设立为中国跨性别追悼日，以缅怀在2022年3月9日因女性化性别表达而被针对进行仇恨犯罪的遇难者<sup class="footnote-ref"><a href="#fn1" id="fnref1">[1]</a></sup>。</p>',12)),o(n),o(r),a[1]||(a[1]=t('<hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="fn1" class="footnote-item"><p><a href="https://weibo.com/ttarticle/p/show?id=2309404811536454516901" target="_blank" rel="noreferrer">《死亡的事实永不结束，生活的篇章终将来到》</a> <a href="#fnref1" class="footnote-backref">↩︎</a></p></li></ol></section>',2))])}const T=s(d,[["render",h]]);export{u as __pageData,T as default};
